import {useRouter} from "next/router"
import {gql,useQuery} from '@apollo/client';
import {useState, useContext} from 'react'
import AppContext from "./context"
import {
  Button,
  Card,
  CardBody,
  CardImg,
  CardText,
  CardTitle,
  Row,
  Col} from "reactstrap";
  import { InputGroup, InputGroupAddon,Input} from "reactstrap";
  
function Dishes(props){
  const [restaurantID, setRestaurantID] = useState()
  const {addItem} = useContext(AppContext);

const GET_RESTAURANT_DISHES = gql`
  query($id: ID!) {
    restaurant(id: $id) {
      id
      name
      dishes {
        id
        name
        description
        price
        image {
          url
        }
      }
    }
  }
`;

  const router = useRouter();

  const { loading, error, data } = useQuery(GET_RESTAURANT_DISHES, {
    variables: { id: props.restId},
  });

  if (loading) return <p>Loading...</p>;
  if (error) return <p>ERROR here</p>;
  if (!data) return <p>Not found</p>;

  let restaurant = data.restaurant;

  //searchquery and then list out

  if (props.restId > 0){
    if(props.query) {

      let searchQuery = restaurant.dishes.filter((res)=>{
        if (res.name.toLowerCase().includes(props.query)){
          return res;
        }
      });

      if(searchQuery.length > 0){

       const dishList = searchQuery.map((res)=>(
            <Col xs="6" sm="4" style={{ padding: 0 }} key={res.id}>
            <Card style={{ margin: "0 10px" }}>
              <CardImg
                top={true}
                style={{ height: 150, width:150 }}
                src={`http://localhost:1337${res.image.url}`}
              />
              <CardBody>
                <CardTitle>{res.name}</CardTitle>
                <CardText>{res.description}</CardText>
              </CardBody>
              <div className="card-footer">
                <Button color="info"
                  outline
                  color="primary"
                  onClick = {()=> addItem(res)}
                >
                  + Add To Cart
                </Button>
                
              </div>
            </Card>
          </Col>
       ));

        return(
          <>
          {dishList}
          </>
        )
      }
      else{
        return <h2>No such dish</h2>;
      }
      
    } 
    else{
      return(
        <>
          {restaurant.dishes.map((res) => (
            <Col xs="6" sm="4" style={{ padding: 0 }} key={res.id}>
              <Card style={{ margin: "0 10px" }}>
                <CardImg
                  top={true}
                  style={{ height: 150, width:150 }}
                  src={`http://localhost:1337${res.image.url}`}
                />
                <CardBody>
                  <CardTitle>{res.name}</CardTitle>
                  <CardText>{res.description}</CardText>
                </CardBody>
                <div className="card-footer">
                  <Button color="info"
                    outline
                    color="primary"
                    onClick = {()=> addItem(res)}
                  >
                    + Add To Cart
                  </Button>
                  
                </div>
              </Card>
            </Col>
          ))}
        </>
      )
    }
  }
    else{
      return "";
    }
    }
    export default Dishes


